  ReactRoot: null,

  onmatch() {
    let root = this.getReactRoot();
    if (!root) {
      root = ReactDOM.createRoot(this[0]);
    }
    root.render(<MyComponent />);
    this.setReactRoot(root);
  },

  onunmatch() {
