Future<void> main() async {
  final database = AppDatabase();

  // Simple select on a table
  final allTodoEntries = await database.select(database.todoItems).get();
  print('Todo entries: $allTodoEntries');

  // More complex select query with where clauses
  final specificTodoEntries = await (database.select(database.todoItems)
        ..where((tbl) => tbl.title.like('%important%')))
      .get();
  print('Specific todo entries: $specificTodoEntries');

  // Writing data
  await database.into(database.todoItems).insert(
      TodoItemsCompanion.insert(
        title: 'A first entry',
        content: 'My content',
      ));
}
