/// Converts between [Preferences] and a [String] representation.
class PreferenceConverter extends TypeConverter<Preferences, String> {
  const PreferenceConverter();

  @override
  Preferences fromSql(String fromDb) {
    return Preferences.fromJson(json.decode(fromDb) as Map<String, Object?>);
  }

  @override
  String toSql(Preferences value) {
    return json.encode(value.toJson());
  }
}

// Note: Consider using TypeConverter.json2 instead, which is a more modern API
// that allows using proper JSON objects in json serialization.
final modernConverter = TypeConverter.json2<Preferences>(
  // to database
  (value) => value.toJson(),
  // from database
  (json) => Preferences.fromJson(json as Map<String, Object?>),
);
