@override
Future<void> updateCart(CartWithItems updatedCart) {
  return transaction(() async {
    // First, delete all existing entries for this cart
    await (delete(cartEntries)..where((e) => e.cart.equals(updatedCart.id)))
        .go();

    // Then, re-create all entries
    for (final item in updatedCart.items) {
      await into(cartEntries).insert(CartEntriesCompanion.insert(
            cart: updatedCart.id,
            product: item.item.id,
            amount: item.amount,
          ));
    }
  });
}
