Future<void> createCategoryForUnassignedTodoEntries() async {
  final newDescription = todos.title.substr(0, 20).as('title');

  // insertFromSelect will run an INSERT INTO ... SELECT FROM ... statement
  await batch((batch) {
    batch.insertFromSelect(
      categories,
      selectOnly(todos)
        ..addColumns([newDescription])
        ..where(todos.category.isNull()),
      columns: {categories.description: newDescription},
    );

    // We also need to update todos with their new category ID.
    // First, category IDs start at 1 and are increased for each new category.
    // We can use COUNT to know how many categories there are.
    final rowCount = selectOnly(categories)
      ..addColumns([categories.id.count()]);
    final startId =
        await rowCount.map((row) => row.read(categories.id.count())).getSingle();

    // Now, we need to figure out which of the original categories we have
    // just created. To do this, we can find the category with a matching title.
    final subquery = selectOnly(todos)
      ..addColumns([todos.id, categories.id])
      ..join([
        innerJoin(
            categories,
            todos.title.like(
                categories.description.concat(const Constant('%'))))
      ])
      ..where(todos.category.isNull());

    final update = update(todos);
    update.where((todo) => existsQuery(
        subquery..where(todos.id.equalsExp(todo.id))));
    update.write(TodosCompanion(category: Value(startId)));
    batch.update(todos, update);
  });
}
```

