function FloatingActionButton({ buttonLetter, index, isExpanded }) {
  const expandedPosition = 70 + index * 60;

  const animatedPositionStyle = useAnimatedStyle(() => {
    const position = withDelay(
      index * 100,
      withTiming(isExpanded.value ? expandedPosition : 10, {
        duration: 150,
      })
    );

    const opacity = withDelay(
      isExpanded.value ? index * 100 : 0,
      withTiming(isExpanded.value ? 1 : 0, {
