import { addons } from '@storybook/manager-api';
import { startCase } from 'lodash';

addons.setConfig({
  sidebar: {
    renderLabel: item => {
      const { name, type, parent } = item;
      if (type === 'story') return startCase(name);
      if (parent?.name === 'components' && type === 'component') return startCase(name);
      return name;
    },
  },
});
