create function public.get_stripe_products(name_prefix text)
returns table (
  id text,
  name text,
  active boolean,
  default_price text,
  description text
)
language plpgsql
security definer set search_path = ''
as $$
begin
  return query
  select
    t.id,
    t.name,
    t.active,
    t.default_price,
    t.description
  from
    stripe.stripe_products t
  where
    t.name like name_prefix || '%'
  ;
end;
$$;
