    public static function loadValidatorMetadata(ClassMetadata $metadata): void
    {
        $metadata->addPropertyConstraint('homepageUrl', new Assert\Url(
            requireTld: true,
            tldMessage: 'Add at least one TLD to the {{ value }} URL.',
