        <doctrine:orm default-entity-manager="default">
            <doctrine:entity-manager name="default" connection="default">
                <doctrine:mapping
                    name="Main"
                    is_bundle="false"
                    dir="%kernel.project_dir%/src/Entity/Main"
                    prefix="App\Entity\Main"
                    alias="Main"
                />
