                    name="Customer"
                    is_bundle="false"
                    dir="%kernel.project_dir%/src/Entity/Customer"
                    prefix="App\Entity\Customer"
                    alias="Customer"
                />
            </doctrine:entity-manager>
        </doctrine:orm>
    </doctrine:config>
</container>
