    metrics/label-template.h
    PARENT_SCOPE)

set(METRICS_SOURCES
    metrics/metrics.c
    metrics/dyn-metrics-store.c
    metrics/dyn-metrics-cache.c
    metrics/dyn-metrics-template.c
