        Router::builder()
            .service_id(service_id)
            .route(
                "square".to_string(),
                Job::new(square)
                    .with_layer(TangleLayer)
                    .with_context(context.clone()),
            )
            .route(
                "multiply".to_string(),
                Job::new(multiply).with_context(context.clone()),
            ),
    )
    .build();
