    public DataTemplate HomeAppointmentTemplate { get; set; }

    protected override DataTemplate OnSelectTemplate(object item, BindableObject container)
    {
        var appointment = item as Appointment;

        if (appointment.Color == Colors.Green)
        {
            return this.BusinessAppointmentTemplate;
        }
        else if (appointment.Color == Colors.DarkOrange)
        {
            return this.HomeAppointmentTemplate;
        }
        else
        {
            return this.DefaultAppointmentTemplate;
        }
    }
}
