```
public class DragDropViewModel
{
    public DragDropViewModel()
    {
        this.TelerikItems = new ObservableCollection<DataModel>();
        this.OtherItems = new ObservableCollection<DataModel>();

        for (int i = 0; i < 15; i++)
        {
            string name = i % 3 == 0 ? "Special Item" : "Item";
            this.TelerikItems.Add(new DataModel() { Name = $"Telerik {name} {i}" });

            if (i < 5)
            {
                this.OtherItems.Add(new DataModel() { Name = $"Other Item {i}" });
            }
        }
    }

    public ObservableCollection<DataModel> TelerikItems { get; set; }
    public ObservableCollection<DataModel> OtherItems { get; set; }
}
