```
public class ViewModel : NotifyPropertyChangedBase
{
    private ObservableCollection<GroupViewModel> groups;

    public ObservableCollection<GroupViewModel> Groups
    {
        get { return this.groups; }
        set { this.UpdateValue(ref this.groups, value); }
    }

    public ICommand GroupItemTapCommand { get; set; }

    public ViewModel()
    {
        var europeCountries = new List<Country>() { new Country() { Name = "Austria" }, new Country() { Name = "Belgium" }, new Country() { Name = "Bulgaria" }, new Country() { Name = "Croatia" } };
        var northAmericaCountries = new List<Country>() { new Country() { Name = "Canada" }, new Country() { Name = "Cuba" }, new Country() { Name = "Greenland" }, new Country() { Name = "Mexico" } };
        var southAmericaCountries = new List<Country>() { new Country() { Name = "Argentina" }, new Country() { Name = "Brazil" }, new Country() { Name = "Chile" }, new Country() { Name = "Colombia" } };

        this.Groups = new ObservableCollection<GroupViewModel>
        {
            new GroupViewModel(europeCountries, "Europe"),
            new GroupViewModel(northAmericaCountries, "North America"),
            new GroupViewModel(southAmericaCountries, "South America")
        };

        this.GroupItemTapCommand = new Command<GroupViewModel>((groupItem) =>
        {
            Application.Current.MainPage.DisplayAlert("GroupTapped", $"Group: {groupItem.Name}", "OK");
        });
    }
}
