private void UpdateLabelTap(object sender, EventArgs e)
{
    var sw = (sender as Grid).Parent as StartSwipeView;
    var item = sw.BindingContext as Person;
    item.Name = "Updated " + item.Name;
    this.collectionView.EndItemSwipe(true);
}

private void DeleteLabelTap(object sender, EventArgs e)
{
    var sw = (sender as Grid).Parent as EndSwipeView;
    var item = sw.BindingContext as Person;

    (this.collectionView.ItemsSource as ObservableCollection<Person>).Remove(item);
    this.collectionView.EndItemSwipe(true);
}
