public class MyGroupStyleSelector : DataGridStyleSelector
{
    protected override DataGridStyle SelectStyle(object item, BindableObject container)
    {
        var group = item as GroupHeaderContext;
        var style = new DataGridStyle();

        if (group.Group.Key.ToString() == "USA")
        {
