```
public class ViewModel : NotifyPropertyChangedBase
{
    private ObservableCollection<Country> countries;

    public ObservableCollection<Country> Countries
    {
        get { return this.countries; }
        set { this.UpdateValue(ref this.countries, value); }
    }

    public ICommand ItemTapCommand { get; set; }

    public ViewModel()
    {
        this.Countries = new ObservableCollection<Country>()
        {
            new Country() { Name = "Argentina", Id = 1 },
            new Country() { Name = "Brazil", Id = 2 },
            new Country() { Name = "Canada", Id = 3 },
            new Country() { Name = "Denmark", Id = 4 },
            new Country() { Name = "England", Id = 5 }
        };

        this.ItemTapCommand = new Command<Country>((country) =>
        {
            Application.Current.MainPage.DisplayAlert("ItemTapped", $"Country: {country.Name}", "OK");
        });
    }
}
