    var finalOffset = e.Offset;

    // Based on the swipe direction and offset, execute different actions
    if (this.collectionView.ItemSwipeDirection == ItemSwipeDirection.End && finalOffset < 0)
    {
        // Handle end-direction swipe
        this.swipeActionText.Text = $"Deleting {item.Name}";
    }
    else if (this.collectionView.ItemSwipeDirection == ItemSwipeDirection.Start && finalOffset > 0)
    {
        // Handle start-direction swipe
        this.swipeActionText.Text = $"Setting {item.Name} as favorite";
    }
}
```

