public partial class ToolbarCustomCommandExample : ContentPage
{
    public ToolbarCustomCommandExample()
    {
        InitializeComponent();

        string fileName = "pdf-sample.pdf";
        string filePath = DependencyService.Get<IFileHelper>().GetLocalFilePath(fileName);
        using (var fileStream = File.OpenRead(filePath))
        {
            MemoryStream stream = new MemoryStream();
            fileStream.CopyTo(stream);
            byte[] bytes = stream.ToArray();
            var source = new Telerik.Maui.Controls.PdfViewer.ByteArrayDocumentSource(bytes, fileName);
            this.pdfViewer.Source = source;
        }

        this.BindingContext = new ToolbarCustomCommandViewModel();
    }
}
```

