<VerticalStackLayout>
	<Label Text="{Binding Value, Source={x:Reference slider}}"/>
	<Slider Value="{Binding Data, Mode=TwoWay}" x:Name="slider" Minimum="5" Maximum="40"/>

	<telerik:RadVerticalGauge x:Name="gauge">
