namespace MvcCS.Controllers
{
    public class ReportsController : ReportsControllerBase
    {
        static ReportServiceConfiguration reportServiceConfigurationValue;

        static ReportsController()
        {
            //This is the folder that contains Reports definitions
            string reportsPath = System.Web.Hosting.HostingEnvironment.MapPath("~/Reports");

            //Uncomment to use the default FileStorage implementation. You may optionally specify a path to persist reports.
            //The default path is in the %TEMP% folder under the user name that the application runs under.
            //The storage folder of the FileStorage component can be set through the constructor parameter:
            //string storagePath = System.Web.Hosting.HostingEnvironment.MapPath("~/ReportCache");
            //var storage = new FileStorage(storagePath);

            //Pass a custom storage implementation (e.g. DatabaseStorage) in order to persist report documents. Note that the storage must be initialized.
            //The initialization requires a connection string to the storage database, and a DDL script for the database schema.
            //Refer to the DatabaseStorage class documentation for more details.
