    <script type="text/javascript">
        $(document).ready(function () {
            var reportName = "",
                hideOptions,
                largeMenuBound = false,
                sideMenuBound = false,
                reportViewer;

            reportViewer = $("#reportViewer1")
                .telerik_ReportViewer({
                    serviceUrl: "api/reports/",
