public class DatabaseReportSourceResolver : IReportSourceResolver
{
    public Telerik.Reporting.ReportSource Resolve(string reportId, OperationOrigin operationOrigin, IDictionary<string, object> parameters)
    {
        var reportIdParts = reportId.Split('/');
        var id = reportIdParts[0];
        var report = GetReportFromDatabase(id);
        var result = new TypeReportSource();
        result.TypeName = report.Type;
        result.Parameters = parameters;
