```
[DataObject]
public class Business
{
    [DataObjectMethod]
    public DataTable GetDataTable()
    {
        DataTable table = new DataTable();
        table.Columns.Add("ID", typeof(int));
        table.Columns.Add("Name", typeof(string));
        table.Rows.Add(1, "John");
        table.Rows.Add(2, "Jane");
        return table;
    }

