                        		},
                        		renderingEnd: function (e, args) {
                            			e.target.commands.export.exec(format);
                        		}
                    		}).data("telerik_ReportViewer");

            		} else {
                		exportReportViewerInstance.reportSource({
                    			report: reportName
                		});
            		}
        	}

		$("#reportViewer1")
            		.telerik_ReportViewer({
	    			serviceUrl: "/api/reports",
				exportBegin: onExportBegin,
				...
	    		})
	})
    </script>
</body>
