Imports System.Collections.Generic

Public Class Car
	Private m_model As String
	Private m_year As Integer

	Public Sub New(model As String, year As Integer)
		Me.m_model = model
		Me.m_year = year
	End Sub

	Public Property Model() As String
		Get
			Return Me.m_model
		End Get
