function claim(
    address _receiver,
    uint256 _tokenId,
    uint256 _quantity
) public payable nonReentrant {
    require(_tokenId < nextTokenIdToMint(), "invalid id");
    verifyClaim(msg.sender, _tokenId, _quantity);

    _transferTokensOnClaim(_receiver, _tokenId, _quantity);
    emit TokensClaimed(msg.sender, _receiver, _tokenId, _quantity);
}
```

