	// send email verification code
	await preAuthenticate({
		client,
		strategy: "email",
		email,
	});
};

const handleLogin = (email: string, verificationCode: string) => {
	// verify email and connect
	connect(async () => {
		const wallet = inAppWallet();
		await wallet.connect({
			client,
			strategy: "email",
			email,
			verificationCode,
		});
		return wallet;
	});
};
