TITLE: Implementing Server-side Authentication Functions in Next.js
DESCRIPTION: Server-side implementation of thirdweb authentication functions in a Next.js application. This code handles payload generation, login verification, JWT generation, and authentication checking using cookies.
SOURCE: https://github.com/thirdweb-dev/js/blob/main/apps/portal/src/app/connect/auth/frameworks/next/page.mdx#2025-04-14_snippet_8

LANGUAGE: tsx
CODE:
```
"use server";
import { VerifyLoginPayloadParams, createAuth } from "thirdweb/auth";
import { privateKeyToAccount } from "thirdweb/wallets";
import { client } from "@/lib/client";
import { cookies } from "next/headers";

const privateKey = process.env.AUTH_PRIVATE_KEY || "";

if (!privateKey) {
	throw new Error("Missing AUTH_PRIVATE_KEY in .env file.");
}

const thirdwebAuth = createAuth({
	domain: process.env.NEXT_PUBLIC_THIRDWEB_AUTH_DOMAIN || "",
	adminAccount: privateKeyToAccount({ client, privateKey }),
	client: client,
});

export const generatePayload = thirdwebAuth.generatePayload;

export async function login(payload: VerifyLoginPayloadParams) {
	const verifiedPayload = await thirdwebAuth.verifyPayload(payload);
	if (verifiedPayload.valid) {
		const jwt = await thirdwebAuth.generateJWT({
			payload: verifiedPayload.payload,
		});
		cookies().set("jwt", jwt);
	}
}

export async function isLoggedIn() {
	const jwt = cookies().get("jwt");
	if (!jwt?.value) {
		return false;
	}

	const authResult = await thirdwebAuth.verifyJWT({ jwt: jwt.value });
	return authResult.valid;
}

export async function logout() {
	cookies().delete("jwt");
}
```

