                name: "v1Policy";
                schema: {
                    type: "object";
                    properties: {
                        condition: {
                            type: "string",
                            description: "A condition expression that evalutes to true or false.",
                            required: true
                        },
                        consensus: {
                            type: "string",
                            description: "A consensus expression that evalutes to true or false.",
                            required: true
                        },
                        createdAt: {
                            $ref: "#/definitions/externaldatav1Timestamp",
                            required: true
                        },
                        effect: {
                            $ref: "#/definitions/v1Effect",
