```
type ActivityResult = {
  createApiKeysResult: { apiKeyId: string; };
  signRawPayloadResult: { r: string; s: string; v: string; };
  signTransactionResult: { signedTransaction: string; };
  updatePolicyResult: { policyId: string; };
  // ... many more result types
};

type ActivityStatus =
  | "ACTIVITY_STATUS_CREATED"
  | "ACTIVITY_STATUS_PENDING"
  | "ACTIVITY_STATUS_COMPLETED"
  | "ACTIVITY_STATUS_FAILED"
  | "ACTIVITY_STATUS_CONSENSUS_NEEDED"
  | "ACTIVITY_STATUS_REJECTED";

type ActivityType =
  | "ACTIVITY_TYPE_CREATE_API_KEYS"
  | "ACTIVITY_TYPE_CREATE_USERS"
  | "ACTIVITY_TYPE_SIGN_RAW_PAYLOAD"
  | "ACTIVITY_TYPE_CREATE_POLICY"
  // ... many more activity types
  | "ACTIVITY_TYPE_INIT_OTP_AUTH_V2";

type Activity = {
  result: ActivityResult;
  status: ActivityStatus;
  type: ActivityType;
  updatedAt: { nanos: string; seconds: string; };
  votes: object[];
};
```

