TITLE: Implementing Private Content Sync in Tldraw Multiplayer (TypeScript/TSX)
DESCRIPTION: This code snippet demonstrates how to create a Tldraw component with private content synchronization for multiplayer sessions. It includes functionality for toggling private mode, managing user presence, and handling shape creation with ownership.
SOURCE: https://github.com/tldraw/tldraw/blob/main/apps/examples/src/examples/sync-private-content/README.md#2025-04-16_snippet_0

LANGUAGE: tsx
CODE:
```
import { TLShape, Tldraw, track, useEditor } from '@tldraw/tldraw'
import '@tldraw/tldraw/tldraw.css'
import { useCallback, useEffect, useState } from 'react'

const PERSIST_KEY = 'tldraw_private_content_example'

export default function SyncPrivateContentExample() {
	const [userId] = useState(() => Math.random().toString(36).slice(2))
	const [isPrivate, setIsPrivate] = useState(false)

	const handleMount = useCallback((editor: Editor) => {
		const savedValue = localStorage.getItem(PERSIST_KEY)
		if (savedValue) {
			try {
				const savedStore = JSON.parse(savedValue)
				editor.store.loadSnapshot(savedStore)
			} catch (err) {
				console.error(err)
			}
		}

		editor.user.updateUserPreferences({ id: userId })

		// Set up callback to save the document when it changes
		const cleanupFn = editor.store.listen(() => {
			localStorage.setItem(PERSIST_KEY, JSON.stringify(editor.store.getSnapshot()))
		})

		return () => {
			cleanupFn()
		}
	}, [])

	return (
		<div className="tldraw__editor">
			<Tldraw
				shapeUtils={[]}
				persistenceKey="example"
				showPages={false}
				showMultiplayerMenu={false}
				streamsPerClient={1}
				onMount={handleMount}
				components={{
					TopPanel: () => (
						<TogglePrivateMode userId={userId} isPrivate={isPrivate} setIsPrivate={setIsPrivate} />
					),
				}}
			>
				<PrivateShapes userId={userId} isPrivate={isPrivate} />
			</Tldraw>
		</div>
	)
}

const TogglePrivateMode = track(function TogglePrivateMode({
	userId,
	isPrivate,
	setIsPrivate,
}: {
	userId: string
	isPrivate: boolean
	setIsPrivate: (isPrivate: boolean) => void
}) {
	const editor = useEditor()

	useEffect(() => {
		const unsub = editor.store.listen(function updatePresence() {
			editor.updateInstanceState({ isPrivate }, { ephemeral: true })
		})
		return () => {
			unsub()
		}
	}, [editor, isPrivate])

	return (
		<div style={{ position: 'absolute', top: 64, left: 12, zIndex: 99999, display: 'flex' }}>
			<div style={{ padding: 8, border: '1px solid black', backgroundColor: 'white' }}>
				User ID: {userId}
			</div>
			<div
				style={{ cursor: 'pointer', padding: 8, border: '1px solid black', backgroundColor: 'white' }}
				onClick={() => setIsPrivate(!isPrivate)}
			>
				Private mode: {isPrivate ? 'ON' : 'OFF'}
			</div>
		</div>
	)
})

const PrivateShapes = track(function PrivateShapes({
	userId,
	isPrivate,
}: {
	userId: string
	isPrivate: boolean
}) {
	const editor = useEditor()

	useEffect(() => {
		const unsub = editor.store.listen(function hidePrivateShapes() {
			for (const shape of editor.store.allShapes()) {
				if (shape.meta.ownerId === userId) {
					if (isPrivate) {
						editor.setOpacity(shape.id, 1)
					} else {
						editor.setOpacity(shape.id, 0.2)
					}
				} else {
					const otherUser = editor.store.get(shape.meta.ownerId)
					if (otherUser?.isPrivate) {
						editor.setOpacity(shape.id, 0)
					} else {
						editor.setOpacity(shape.id, 1)
					}
				}
			}
		})
		return () => {
			unsub()
		}
	}, [editor, isPrivate, userId])

	const handleShapeCreated = useCallback(
		(shape: TLShape) => {
			editor.updateShape({
				id: shape.id,
				type: shape.type,
				meta: { ...shape.meta, ownerId: userId },
			})
		},
		[editor, userId]
	)

	useEffect(() => {
		return editor.on('shape-created', handleShapeCreated)
	}, [editor, handleShapeCreated])

	return null
})
```

