TITLE: Subscription Cleanup Pattern
DESCRIPTION: Shows how to implement cleanup of side effects in tRPC subscriptions using try...finally pattern.
SOURCE: https://github.com/trpc/trpc/blob/main/www/docs/server/subscriptions.md#2025-04-23_snippet_3

LANGUAGE: typescript
CODE:
```
import EventEmitter, { on } from 'events';
import type { Post } from '@prisma/client';
import { z } from 'zod';
import { publicProcedure, router } from '../trpc';

const ee = new EventEmitter();

export const subRouter = router({
  onPostAdd: publicProcedure.subscription(async function* (opts) {
    let timeout;
    try {
      for await (const [data] of on(ee, 'add', {
        signal: opts.signal,
      })) {
        timeout = setTimeout(() => console.log('Pretend like this is useful'));
        const post = data as Post;
        yield post;
      }
    } finally {
      if (timeout) clearTimeout(timeout);
    }
  }),
});
