LANGUAGE: typescript
CODE:
```
import {Use} from "@tsed/common";
import {useDecorators} from "@tsed/core";
import {StoreMerge} from "@tsed/core";
import {PlatformAcceptMimesMiddleware} from "../middlewares/PlatformAcceptMimesMiddleware";

/**
 * Accept only requests with the Content-type that match with given mime types.
 *
 * @param mimes
 * @decorator
 * @operation
 * @controller
 */
export function AcceptMime(...mimes: string[]) {
  const normalizedMimes = mimes.map((mime) => mime.toLowerCase());

  return useDecorators(
    StoreMerge(PlatformAcceptMimesMiddleware, normalizedMimes),
    Use(PlatformAcceptMimesMiddleware)
  );
}
```

