LANGUAGE: typescript
CODE:
```
import {Controller, Get} from "@tsed/common";
import {Returns} from "@tsed/schema";
import {Document} from "./Document";
import {Product} from "./Product";

@Controller("/products")
export class ProductsController {
  @Get("/:id")
  @Returns(200, Document).Of(Product).Description("Get product detail")
  async get(@PathParams("id") id: string): Promise<Document<Product>> {
    return new Document<Product>({
      data: new Product({
        id,
        title: "title"
      }),
      links: {
        self: `/products/${id}`,
        products: "/products"
      }
    });
  }
}

export class Document<T> {
  constructor({data, links}: Partial<Document<T>> = {}) {
    if (data) {
      this.data = data;
    }

    if (links) {
      this.links = links;
    }
  }

  data: T;
  links: {
    self: string;
    [key: string]: string;
  };
}

export class Product {
  @Property()
  id: string;

  @Property()
  title: string;

  constructor({id, title}: Partial<Product> = {}) {
    id && (this.id = id);
    title && (this.title = title);
  }
}
```

