LANGUAGE: typescript
CODE:
```
import {CustomAuth} from "../middlewares/CustomAuth";
import {UseAuth} from "@tsed/common";
import {Returns, Security} from "@tsed/schema";

export function Auth(options?: any) {
  return (target: any, propertyKey: string, descriptor: PropertyDescriptor) => {
    // Apply decorators
    Security("bearer")(target, propertyKey, descriptor);
    Returns(401).Description("Unauthorized")(target, propertyKey, descriptor);
    Returns(403).Description("Forbidden")(target, propertyKey, descriptor);
    UseAuth(CustomAuth, options)(target, propertyKey, descriptor);

    return descriptor;
  };
}
```

