  uploadFile(@MultipartFile("file") file: PlatformMulterFile) {
    console.log("Uploaded", file);

    return {name: file.originalname};
  }

  @Post("/upload-with-error")
  uploadFileWithError(@MultipartFile("file") file: PlatformMulterFile, @Req() req: Req) {
