import {Middleware, Context, EndpointInfo, Req, Res, Next} from "@tsed/common";
import {Unauthorized} from "@tsed/exceptions";

@Middleware()
export class AcceptMimesMiddleware {
  use(@EndpointInfo() endpoint: EndpointInfo, @Req() request: Req, @Res() response: Res) {
    const mimes = endpoint.get(AcceptMimes) || [];

    if (!request.accepts(mimes)) {
      throw new Unauthorized("Unsupported Media Type");
    }
  }
}
