```
import {StoreSet} from "@tsed/core";
import {useDecorators} from "@tsed/core";
import {UseAfter} from "@tsed/common";

export function Status(status: number) {
  return useDecorators(
    StoreSet(Status, status),
    UseAfter((req: any, res: any, next: any) => {
      const endpoint = req.ctx.endpoint;
      const code = endpoint.get(Status) || res.statusCode;

      res.status(code);
      next();
    })
  );
}
