TITLE: PrimitiveMapper Implementation for Type Conversion
DESCRIPTION: The implementation of PrimitiveMapper that handles conversion between primitive types like Boolean, Number, and String during serialization and deserialization.
SOURCE: https://github.com/tsedio/tsed/blob/production/docs/docs/json-mapper.md#2025-04-16_snippet_8

LANGUAGE: typescript
CODE:
```
import {JsonMapper} from "../decorators/jsonMapper";
import {JsonMapperMethods} from "../interfaces/JsonMapperMethods";

@JsonMapper([
  Boolean,
  String,
  Number
] as any[])
export class PrimitiveMapper implements JsonMapperMethods {
  deserialize<T>(obj: any, targetType: T): any {
    if (obj === null || obj === undefined) {
      return obj;
    }

    const constructor = targetType as any;

    if (constructor === String) {
      return String(obj);
    }

    if (constructor === Boolean) {
      if (obj === "null") {
        return null;
      }

      if (obj === true || obj === "true" || obj === "1" || obj === 1) {
        return true;
      }

      if (obj === false || obj === "false" || obj === "0" || obj === 0 || obj === "") {
        return false;
      }

      return Boolean(obj);
    }

    if (constructor === Number) {
      if (obj === "null") {
        return null;
      }

      if (["NaN", NaN].includes(obj)) {
        return NaN;
      }

      if (/^-?\d+$/.test(String(obj))) {
        return parseInt(String(obj), 10);
      }

      // if it's a numeric with precision
      if (/^-?\d+\.\d+$/.test(String(obj))) {
        return parseFloat(String(obj));
      }

      const num = Number(obj);

      if (isNaN(num)) {
        throw new Error(`Cast error. Cannot cast value \'${obj}\' to Number.`);
      }

      return num;
    }

    return obj;
  }

  serialize(object: any): any {
    return object;
  }
}
```

