  post(@BodyParams() user: any, @Context() ctx: RequestContext) {
    // Create a new context from existing context
    const myContext: MyContext = {...ctx, userId: user.id};
    setContext("myContext", myContext);

    return "..."; // do something
  }

  @Get("/profile")
  getProfile(@MyContext() context: MyContext) {
