CODE:
```
import {Inject, Injectable} from "@tsed/di";
import {Adapter} from "@tsed/adapters";
import {OnInit} from "@tsed/components";
import {Account} from "../models/Account";

@Injectable()
export class Accounts implements OnInit {
  @Inject()
  protected adapter: Adapter;

  async $onInit() {
    await this.adapter.create("accounts", {
      id: "1",
      email: "test@test.com",
      email_verified: true
    });
  }

  async findAccount(id: string, token: any): Promise<Account | undefined> {
    const account = await this.adapter.findById("accounts", id);

    if (!account) {
      return undefined;
    }

    return new Account(account);
  }

  async createAccount(account: Account): Promise<Account> {
    const item = await this.adapter.create("accounts", account);

    return new Account(item);
  }
}
```

