LANGUAGE: typescript
CODE:
```
@Interaction({
  name: "abort"
})
export class AbortInteraction {
  @Inject()
  protected oidc: OidcService;

  async $exec(oidcCtx: OidcCtx) {
    const result = {
      error: "access_denied",
      error_description: "End-User aborted interaction"
    };

    return this.oidc.interactionFinished(oidcCtx, result, { mergeWithLastSubmission: false });
  }
}
