import {Get, registerProvider} from "@tsed/common";
import {inject} from "@tsed/di";
import {useDecorators} from "@tsed/core";

export class MyService {
  random = Math.random();

  get() {
    return this.random;
  }
}

registerProvider({
  provide: MyService,
  scope: "singleton"
});

function RandomController() {
  const myService = inject(MyService);

  return useDecorators(
    Get("/"),
    () => {
      return myService.get();
    }
  );
}

registerProvider({
  provide: RandomController,
  type: "controller",
  use: [RandomController], // function and endpoint
  scope: "request"
});
