const editor = new BlockNoteEditor({
  // ...
  inlineContentTypes: {
    mention: {
      type: "mention",
      propSchema: {
        user: {
          default: { id: "", name: "" },
        },
      },
      content: true,

      render: (props) => {
        const element = document.createElement("span");
        element.className = "mention";
        element.textContent = `@${props.inlineContent.props.user.name}: ${props.content}`;
        element.contentEditable = "false";

        element.addEventListener("click", () => {
          // Show UI for editing the mention
          const newContent = prompt("Enter new content", props.content);
          if (newContent !== null) {
            props.updateContent(newContent);
          }
        });

        return element;
      },

      insertMenuItems: (editor) => [
        {
          name: "User Mention",
          execute: () => {
            editor.insertInlineContent("mention", {
              user: { id: "1", name: "John Doe" },
            }, "Initial content");
          },
        },
      ],
    },
  },
});
```

