TITLE: Custom Side Menu Implementation
DESCRIPTION: Complete example of implementing a custom side menu UI element, including positioning, styling, and event handling for block manipulation.
SOURCE: https://github.com/typecellos/blocknote/blob/main/docs/pages/docs/advanced/vanilla-js.mdx#2025-04-18_snippet_3

LANGUAGE: typescript
CODE:
```
import { BlockNoteEditor } from "@blocknote/core";

const editor = BlockNoteEditor.create({
  element: document.getElementById("root")!
});

export function createButton(text: string, onClick?: () => void) {
  const element = document.createElement("a");
  element.href = "#";
  element.text = text;
  element.style.margin = "10px";

  if (onClick) {
    element.addEventListener("click", (e) => {
      onClick();
      e.preventDefault();
    });
  }

  return element;
}

let element: HTMLElement;

editor.sideMenu.onUpdate((sideMenuState) => {
  if (!element) {
    element = document.createElement("div");
    element.style.background = "gray";
    element.style.position = "absolute";
    element.style.padding = "10px";
    element.style.opacity = "0.8";
    const addBtn = createButton("+", () => {
      editor.sideMenu.addBlock();
    });
    element.appendChild(addBtn);

    const dragBtn = createButton("::", () => {});

    dragBtn.addEventListener("dragstart", editor.sideMenu.blockDragStart);
    dragBtn.addEventListener("dragend", editor.sideMenu.blockDragEnd);
    dragBtn.draggable = true;
    element.style.display = "none";
    element.appendChild(dragBtn);

    document.getElementById("root")!.appendChild(element);
  }

  if (sideMenuState.show) {
    element.style.display = "block";

    element.style.top = sideMenuState.referencePos.top + "px";
    element.style.left =
      sideMenuState.referencePos.x - element.offsetWidth + "px";
  } else {
    element.style.display = "none";
  }
});
```

