      <div className="flex max-w-screen-xl flex-col items-center px-6 lg:px-8">
        <h2 className="max-w-6xl text-center text-base font-semibold leading-7 text-indigo-600">
          BlockNote Pro
        </h2>
        <SectionHeader>Upgrade your BlockNote experience</SectionHeader>
        <p className="mt-6 max-w-screen-lg text-center text-lg text-[#00000080] dark:text-[#FFFFFFB2]">
          Thanks for checking out BlockNote Pro! BlockNote is free and open source
          software that organizations of all sizes are using to add polished editing
          experiences to their apps.
        </p>
        <p className="mt-6 max-w-screen-lg text-center text-lg text-[#00000080] dark:text-[#FFFFFFB2]">
          With your subscription, you are ensuring the sustainable maintenance and
          development of the library. It's a great way to support the project and
          get access to direct support, prioritized feature requests and Pro
          Examples.
        </p>
        <Tiers tiers={tiers} frequency={frequency} />
        <FAQ />
      </div>
    </div>
  );
}
