  ],
  parser: '@typescript-eslint/parser',
  parserOptions: {
    // Remove this line
    project: ['./tsconfig.eslint.json', './**/tsconfig.json'],
    // Add this line
    project: ['./tsconfig.eslint.json', './packages/*/tsconfig.json'],
